function output = convertSyndromeZ(flagM,dist,time)% This function takes as input the matrix representing the flagged syndromes% and converts it to a list of coordinated in space-time for the decodernumflags = sum(sum(flagM));if numflags > 0    output = zeros(numflags,3);    counter = 1;    for i = 1:(dist-1)        for j = 1:((dist-1)/2+1)            if flagM(i,j) == 1                output(counter,:) = [2*j-mod(i,2),i+1,time];                counter = counter + 1;            end                end    endelse    output = [];    endend